  const filteredItems = items.filter(items => {
      console.log(items.name.includes(nameFilter)); 
      return true;
    });

  return (
    <>
    <Header />
    <Filters handleFilter={handleFilter} />
    <CharacterList items={filteredItems}/>
    <CharacterDetail />
    </>
  );
};

export default App;
